#plot multivariate BF against MANOVA for simulations generated by simset
all.vs.man.compplot=function(sim,...){
t=sim$t.04
nrep = length(t)
lbfall = rep(0,nrep)
lbfuni = rep(0,nrep)
for(j in 1:nrep){lbfall[j]=t[[j]]$lbfall;
                lbfuni[j] = -log10(sim$m[[j]]);}
xmax = max(c(lbfall,lbfuni))
xmin = min(c(lbfall,lbfuni))
if(xmax<12){xmax=12}
if(xmin>-1){xmin=-1}
plot(lbfuni,lbfall,xlim=c(xmin,xmax),ylim=c(xmin,xmax),xlab="-log10(MANOVA p value)",ylab="log10(BFall)",...)
abline(a=0,b=1)
abline(v=4,h=4,lty=2)
}

